jQuery(document).ready(function($) {
    $('.donation-module__options--periods .donation-module__options__option__input').on('change', function() {
        var _this = $(this);
        var _values = $('.donation-module__options--values');
        var target = _this.data('target');
        var _target = $('#donation-module_options--' + target);
        var defaultAmount = _this.data('default');

        _values.hide();
        $('#donation-module-other-amount').appendTo($('.donation-module__options__option__other', _target));
        _target.show().css('display', 'flex').find('.donation-module__options__option__input').each(function() {
            if (parseInt($(this).val()) === parseInt(defaultAmount)) {
                $('.donation-module__options__option__input', _values).removeAttr('checked');
                $(this).trigger('click');
            }
        });
    });

    $('.donation-module__options__option__input').on('click', function(e) {
        var _this = $(this);

        if (_this.hasClass('other')) {
            $(this).parents('.donation-module__options__option').addClass('active').find('.money-input').focus();
        }
        else {
            $('.donation-module__options__option.active').removeClass('active');
        }
    });

});